const NEW_YEAR_TOYS=document.querySelectorAll(".new-year__toy");function nyToyHoverHandler(e){let t=e.target;if(t.classList.contains("animate"))return;t.classList.add("animate");let n=setTimeout(function(){t.classList.remove("animate"),clearTimeout(n)},1450)}NEW_YEAR_TOYS.forEach(function(e){e.addEventListener("mouseenter",nyToyHoverHandler),e.addEventListener("touchstart",nyToyHoverHandler)});