const NEW_YEAR_TOYS=document.querySelectorAll(".new-year__toy");function nyToyHoverHandler(e){if(e.target.classList.contains("animate"))return;e.target.classList.add("animate");let t=setTimeout(function(){e.target.classList.remove("animate"),clearTimeout(t)},2e3)}NEW_YEAR_TOYS.forEach(function(e){e.addEventListener("mouseenter",nyToyHoverHandler),e.addEventListener("touchstart",nyToyHoverHandler)});